import React from "react";
import {
  Box,
  Image,
  Link,
  useDisclosure,
  useMediaQuery,
} from "@chakra-ui/react";

import logo from "../images/logo.svg";
import menu from "../images/icon-menu.svg";

import MobileMenu from "./MobileMenu";

const LinkItems = [
  { label: "Home", href: "/" },
  { label: "Trending", FiThref: "/" },
  { label: "Explore", href: "/" },
  { label: "Favourites", href: "/" },
  { label: "Settings", href: "/" },
];

type MobileMenuProp = {
  //   onClose: () => void;
  display: any;
};

const Header = () => {
  const [isLargerThan686] = useMediaQuery("(min-width: 686px)");
  const { isOpen, onOpen, onClose } = useDisclosure();
  return (
    <>
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        height="28px"
        maxWidth={{ base: "100%", sm: "100%", md: "100%", lg: "1110px" }}
        mx={{ base: "auto" }}
        my="28px"
      >
        <Image src={logo} width="45px" />
        <Box minW="441px" flexDirection="row" justifyContent="space-evenly">
          {isLargerThan686 &&
            LinkItems.map((link, i) => (
              <Link key={i} fontSize="15px" fontWeight="400" lineHeight="26px">
                {link?.label}
              </Link>
            ))}
        </Box>

        <Image
          src={menu}
          width="40px"
          height="17px"
          display={{ base: "auto", sm: "auto", md: "none", lg: "none" }}
        />
      </Box>
    </>
  );
};

export default Header;
