import React from "react";

import { Image, Text, Stack } from "@chakra-ui/react";

type ItemProp = {
  heading: string;
  subHeading: string;
  body: string;
};

type ItemProps = {
  image?: string;
  data?: ItemProp;
};

const StoryItem = ({ image, data }: ItemProps) => {
  console.log(data);

  return (
    <>
      <Image boxSize="100px" objectFit="cover" src={image} alt="image" />
      <Stack alignItems="start">
        <Text
          as="h3"
          color="#C5C6CE"
          fontSize="32px"
          fontWeight="extrabold"
          lineHeight="32px"
        >
          {data?.heading}
        </Text>
        <Text
          as="h4"
          color="#00001A"
          fontSize="18px"
          fontWeight="extrabold"
          lineHeight="24px"
        >
          {data?.subHeading}
        </Text>
        <Text
          as="p"
          color="#5E607A"
          fontSize="15px"
          lineHeight="26px"
          fontWeight="normal"
        >
          {data?.body}
        </Text>
      </Stack>
    </>
  );
};

export default StoryItem;
