import React from "react";
import imageDesktop from "../images/image-web-3-desktop.jpg";
import imageMobile from "../images/image-web-3-mobile.jpg";
import {
  Box,
  Button,
  Flex,
  Heading,
  Image,
  Link,
  Text,
  Stack,
  useMediaQuery,
  VStack,
} from "@chakra-ui/react";

const LinkItems = [
  { label: "Home", href: "/" },
  { label: "Trending", FiThref: "/" },
  { label: "Explore", href: "/" },
  { label: "Favourites", href: "/" },
  { label: "Settings", href: "/" },
];

type MobileMenuProp = {
  onClose: () => void;
  display: any;
};

const MobileMenu = ({ onClose }: MobileMenuProp) => {
  return (
    <Box
      backgroundColor="#333"
      transition="3s ease"
      //   bg="white"
      //   borderRight="1px"
      borderRightColor="red.800"
      w={{ base: "full", md: 60 }}
      zIndex={1}
    >
      <Flex h="20" alignItems="center" mx="8" justifyContent="space-between">
        <Text fontSize="2xl" fontFamily="monospace" fontWeight="bold">
          Logo
        </Text>
        <Button display={{ base: "flex", md: "none" }} onClick={onClose} />
      </Flex>
      {LinkItems.map((link, i) => (
        <Link key={i}>{link?.label}</Link>
      ))}
    </Box>
  );
};

export default MobileMenu;
